// packages/ui/components/Topbar.tsx
"use client";

import * as React from "react";
import { User } from "lucide-react";

interface TopbarProps {
  user: {
    name?: string | null;
    email?: string | null;
  };
}

export function Topbar({ user }: TopbarProps) {
  // Get initials safely
  const getInitials = (name?: string | null) =>
    name
      ? name
          .split(" ")
          .map((n) => n[0])
          .join("")
          .toUpperCase()
          .slice(0, 2)
      : "";

  return (
    <div className="h-20 flex items-center justify-between px-6 lg:px-8 bg-transparent">
      
      {/* Title */}
      <h1 className="text-2xl font-bold bg-gradient-to-r from-indigo-600 via-purple-600 to-pink-600 bg-clip-text text-transparent tracking-tight">
        Task Manager
      </h1>

      {/* User Info */}
      <div className="flex items-center gap-3">
        {/* Avatar */}
        <div className="w-10 h-10 rounded-full bg-gradient-to-br from-indigo-600 via-purple-600 to-pink-600 flex items-center justify-center text-white font-bold">
          {getInitials(user.name) || <User className="w-5 h-5" />}
        </div>

        {/* User name & email */}
        <div className="hidden md:flex flex-col text-left min-w-0">
          <span className="text-sm font-semibold text-gray-900 dark:text-white truncate">
            {user.name || "User"}
          </span>
          <span className="text-xs text-gray-500 dark:text-gray-400 truncate">
            {user.email || "email@example.com"}
          </span>
        </div>
      </div>
    </div>
  );
}
